package ControlFlow;

public class PositiveNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 0;
		
		if(a>0) {
			System.out.println("Positive");
		}else if(a<0) {
			System.out.println("Negative");
		}else {
			System.out.println("number is zero");
		}
	}

}
